const Menu = () => {
  const isMobile = useIsMobile();

  return (
    <Sidebar className="">
      <SidebarContent>
        {/* logo */}
        <Link
          to="/"
          aria-label="home"
          className="flex items-center justify-center pb-4 pt-12"
        >
          <div className="w-[100px] md:w-[180px] h-[58px]">
            <img
              src={logo}
              alt="EcoCollect logo"
              className="w-full h-full object-contain"
            />
          </div>
        </Link>

        {/* line */}
        <div className="h-[1px] bg-[#F5F5F5] w-full " />

        <SidebarGroup className="py-6 px-4 pb-[80px]">
          <SidebarGroupContent>
            <SidebarMenu className="gap-6">
              {primaryMenuItems.map((item) => (
                <SidebarMenuItem key={item.title} className="">
                  <SidebarMenuButton asChild>
                    <NavLink
                      to={item.url}
                      className={({ isActive }) =>
                        cn(
                          "text-black text-2xl font-normal capitalize py-6  ",
                          "hover:bg-transparent",
                          isActive ? "border-l-4 " : " "
                        )
                      }
                    >
                      <span>{item.icon}</span>
                      <span>{item.title}</span>
                    </NavLink>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        {/* line */}
        <div className="h-[1px] bg-[#F5F5F5] w-full " />

        {/* settings & logout */}
        <SidebarGroup className="py-6 px-4">
          <SidebarGroupContent>
            <SidebarMenu className="gap-6">
              {userMenuItems.map((item) => (
                <SidebarMenuItem key={item.title} className="">
                  <SidebarMenuButton asChild>
                    <NavLink
                      to={item.url}
                      className={({ isActive }) =>
                        cn(
                          "text-black text-2xl font-normal capitalize py-6  ",
                          "hover:bg-transparent",
                          isActive ? "border-l-4 " : " "
                        )
                      }
                      aria-label={item.title}
                    >
                      <span>{item.icon}</span>
                      {!isMobile && <span>{item.title}</span>}
                    </NavLink>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
};
export default Menu;
